mapper.select:
select bi.block_id, bi.grid_id, bi.drug_id, ddi.drug_inf_id, di.drug_name
from drug_cart_db.block_inf bi
left join drug_cart_db.drug_date_inf ddi
on bi.drug_id = ddi.drug_id
left join drug_inf di
on ddi.drug_inf_id = di.drug_inf_id
where 1 = 1

    and bi.drug_id = #{drugId}


    and bi.grid_id = #{gridId}


    limit #{start},#{pageSize}

select count(bi.block_id) from drug_cart_db.block_inf bi left join drug_cart_db.drug_date_inf ddi on bi.drug_id = ddi.drug_id left join drug_inf di on ddi.drug_inf_id = di.drug_inf_id where 1 = 1
and bi.drug_id = #{drugId}
and bi.grid_id = #{gridId}

mapper.insert:
insert into drug_cart_db.block_inf (
    drug_id, 
    grid_id
)
values(
    #{drugId}
    #{gridId}
)
