mapper.select:
select ci.cart_id, ci.department_id, ci.add_time, ci.status_type, di.department_name
from carts_inf ci
left join department_inf di on ci.department_id = di.department_id
where ci.is_enable = 1

    and di.department_name like #{departmentName}


    and ci.department_id = #{departmentId}


    and status_type = #{state}


    limit #{start},#{pageSize}

mapper.insert:
insert into carts_inf(
                      department_id
                      , add_time
                      , status_type
                    )
values(
       #{departmentId}
       , #{addTime}
       , #{state}
       )

mapper.update:
update carts_inf
set is_enable=0
where cart_id = #{id};

update carts_inf
department_id = #{departmentId}, add_time = #{addTime}, status_type = #{state},
where cart_id = #{id} and isEnable=1;
