mapper.select:
select drug_id, drug_barcode, drug_name, drug_product_date, drug_shelf_life, drug_stock, drug_package
from drug_cart_db.drug_inf
where 1 = 1

    and drug_name like #{nameCondition}


    and drug_barcode = #{barcode}

select drug_id, drug_barcode, drug_name, drug_product_date, drug_shelf_life, drug_stock, drug_package
from drug_cart_db.drug_inf
where drug_id = #{id}

select count(drug_id)
from drug_cart_db.drug_inf
where 1 = 1

    and drug_name like #{nameCondition}


    and drug_barcode = #{barcode}

mapper.insert:
insert into drug_cart_db.drug_inf(
    drug_barcode
    drug_name
    drug_product_date
    drug_shelf_life
    drug_stock
    drug_package
)
values(
    #{barcode}
    #{name}
    #{productDate}
    #{shelfLife}
    #{stock}
    #{pack}
)

mapper.update:
update drug_cart_db.drug_inf

    drug_barcode=#{barcode}
    drug_name=#{name}
    drug_product_date=#{productDate}
    drug_shelf_life=#{shelfLife}
    drug_stock=#{stock}
    drug_package=#{pack}

where drug_id = #{id}
