mapper.select:
select ddi.drug_id, di.drug_inf_id, di.drug_barcode, di.drug_name, ddi.drug_product_date, di.drug_shelf_life, ddi.drug_stock, di.drug_package
from drug_cart_db.drug_date_inf ddi
inner join drug_cart_db.drug_inf di
on di.drug_inf_id = ddi.drug_inf_id
where 1 = 1

    and di.drug_name like #{nameCondition}


    and di.drug_barcode = #{barcode}


    limit #{start},#{pageSize}

select count(ddi.drug_id) from drug_cart_db.drug_date_inf ddi inner join drug_cart_db.drug_inf di on di.drug_inf_id = ddi.drug_inf_id where 1 = 1
and drug_name like #{nameCondition}
and drug_barcode = #{barcode}

mapper.insert:
insert into drug_cart_db.drug_date_inf(
    drug_inf_id,
    drug_product_date
    , drug_stock
)
values(
    #{infId},
    #{productDate}
    , #{stock}
)

mapper.update:
update drug_cart_db.drug_date_inf

    drug_inf_id=#{infId}
    drug_product_date=#{productDate}
    drug_stock=#{stock}

where drug_id = #{id}
