mapper.select:
select ei.employee_id, ei.employee_name, ei.employee_phone, ei.employee_add_time, ei.department_id, di.department_name, ei.enable_flag
from employees_inf ei
left join department_inf di on ei.department_id = di.department_id
where 1 = 1

    and employee_name like #{nameCondition}


    limit #{start},#{pageSize}

mapper.insert:
insert into employees_inf(
employee_name
, employee_phone
, employee_add_time
, department_id
, enable_flag
, password_md5
)
values(
#{name}
, #{phone}
, #{addTime}
, #{departmentId}
, #{isEnable}
, #{password}

mapper.update:
update employees_inf

    employee_name=#{name}
    employee_phone=#{phone}
    employee_addTime=#{addTime}
    department_id=#{departmentId}
    enable_flag=#{isEnable}
    password_md5=#{password}

where employee_id=#{id}
