mapper.select:
select ei.employee_id, ei.employee_name, ei.employee_phone, ei.employee_add_time, ei.department_id, di.department_name, ei.role
from employees_inf ei
left join department_inf di on ei.department_id = di.department_id
where ei.enable_flag=1

    and ei.employee_name like #{nameCondition}


    limit #{start},#{pageSize}

select count(employee_id) from employees_inf
where enable_flag=1;

    and employee_name like #{nameCondition}

mapper.insert:
insert into employees_inf(
employee_name
, employee_phone
, employee_add_time
, department_id
, password_md5
, role
)
values(
#{name}
, #{phone}
, #{addTime}
, #{departmentId}
, #{password}
, #{role}

mapper.update:
update employees_inf

    employee_name=#{name}
    employee_phone=#{phone}
    employee_addTime=#{addTime}
    department_id=#{departmentId}
    password_md5=#{password}
    role=#{role}

where employee_id=#{id} and enable_flag=1;

update employees_inf
set enable_flag=0
where employee_id=#{id}
