mapper.select:
select grid_id, cart_id, cart_layer, cart_row, cart_column, drug_inf_id
from drug_cart_db.grid_inf
where 1 = 1

    and drug_inf_id = #{drugInfId}


    and cart_id = #{cartId}

        and cart_layer = #{layer}



    limit #{start - 1},#{pageSize}

select grid_id, cart_id, cart_layer, cart_row, cart_column, drug_inf_id
from drug_cart_db.grid_inf
where grid_id = #{id}

select grid_id, cart_id, cart_layer, cart_row, cart_column, drug_inf_id
from drug_cart_db.grid_inf
where cart_id = #{cartId}
    and cart_layer = #{layer}
    and cart_row = #{row}
    and cart_column = #{column}

select count(grid_id)
from drug_cart_db.grid_inf
where 1 = 1

    and drug_inf_id = #{drugInfId}


    and cart_id = #{cartId}

        and cart_layer = #{layer}

mapper.insert:
insert into drug_cart_db.grid_inf (
    cart_id, cart_layer, cart_row, cart_column
    , capacity
    , drug_inf_id
)
values (
    #{cartId}, #{layer}, #{row}, #{column}
    , #{capacity}
    , #{drugInfId}
        )
